                if (err) throw err;
                // Set password to hashed
                newDonor.password = hash;
                var sql =
                  "INSERT INTO Donor(name, ssn, gender, birthday, phone, email, address, password) Values (?, ?, ?, ?, ?, ?, ?, ?)";
